// =========================
// Tile:  Real-Time Sensor Status with Z-Score Analysis
// Purpose: Show the latest sensor readings with statistical anomaly detection (zâ€‘scores)
// Refresh cadence: Every 30 seconds
// =========================

// -------------------------
// 1) Configuration & Parameters
// -------------------------
let BaselineWindow          = 30d;       // Statistical baseline lookback
let BaselineExcludeRecent   = 24h;       // Exclude latest 24h to stabilize baseline
let RedZThreshold           = 2.0;       // z-score threshold for Red status
let YellowZThreshold        = 1.5;       // z-score threshold for Yellow status
let DefectRedThreshold      = 0.05;      // DefectProbability threshold for Red
let DefectYellowThreshold   = 0.02;      // DefectProbability threshold for Yellow

// Optional single-asset filter. Leave empty ("") to show all.
let AssetFilterParam        = tostring(['AssetFilter']); // expects a string or empty

// -------------------------
// 2) Asset mapping (Id -> Name)
// -------------------------
let assetMapping =
    assets
    | project AssetId = Id, AssetName = Name;

// -------------------------
// 3) Baseline statistics for anomaly detection
//    Build stats over historical window while excluding the most recent 24h
// -------------------------
let sensorBaseline =
    events
    | join kind=inner (assetMapping) on AssetId
    | where Timestamp >= ago(BaselineWindow) and Timestamp <= ago(BaselineExcludeRecent)
    | where isempty(AssetFilterParam) or AssetName == AssetFilterParam
    | summarize
        SpeedMean       = avg(Speed),
        SpeedStdev      = stdev(Speed),
        TempMean        = avg(Temperature),
        TempStdev       = stdev(Temperature),
        VibrationMean   = avg(Vibration),
        VibrationStdev  = stdev(Vibration),
        DefectMean      = avg(DefectProbability),
        DefectStdev     = stdev(DefectProbability)
      by AssetId;

// -------------------------
// 4) Latest readings in requested time range (_startTime/_endTime)
//    Use arg_max to get the most recent event per asset
// -------------------------
events
| join kind=inner (assetMapping) on AssetId
| where Timestamp >= _startTime and Timestamp <= _endTime
| where isempty(AssetFilterParam) or AssetName == AssetFilterParam
| summarize arg_max(Timestamp, *) by AssetId
| join kind=leftouter (sensorBaseline) on AssetId
// -------------------------
// 5) Z-score calculations (null-safe)
//    z = |x - mean| / stdev; 0 if stats are missing or stdev <= 0
// -------------------------
| extend
    SpeedZScore = iff(isnotnull(SpeedStdev)      and SpeedStdev      > 0, todecimal(abs(Speed        - SpeedMean)      / SpeedStdev),      todecimal(0)),
    TempZScore  = iff(isnotnull(TempStdev)       and TempStdev       > 0, todecimal(abs(Temperature  - TempMean)       / TempStdev),       todecimal(0)),
    VibZScore   = iff(isnotnull(VibrationStdev)  and VibrationStdev  > 0, todecimal(abs(Vibration    - VibrationMean)  / VibrationStdev),  todecimal(0)),
    DefZScore   = iff(isnotnull(DefectStdev)     and DefectStdev     > 0, todecimal(abs(DefectProbability - DefectMean) / DefectStdev),    todecimal(0))
// -------------------------
// 6) Status assignment (emoji bands)
//    Uses z-score thresholds for Speed/Temp/Vibration and raw probability for Defect
// -------------------------
| extend
    SpeedStatus     = case(SpeedZScore > RedZThreshold,   "ðŸ”´",
                           SpeedZScore > YellowZThreshold, "ðŸŸ¡", "ðŸŸ¢"),
    TempStatus      = case(TempZScore  > RedZThreshold,   "ðŸ”´",
                           TempZScore  > YellowZThreshold, "ðŸŸ¡", "ðŸŸ¢"),
    VibrationStatus = case(VibZScore   > RedZThreshold,   "ðŸ”´",
                           VibZScore   > YellowZThreshold, "ðŸŸ¡", "ðŸŸ¢"),
    DefectStatus    = case(DefectProbability > DefectRedThreshold,   "ðŸ”´",
                           DefectProbability > DefectYellowThreshold, "ðŸŸ¡", "ðŸŸ¢")
// -------------------------
// 8) Presentation: columns & formatting
// -------------------------
| project
    Asset = AssetName,
    ["Speed"]               = strcat(SpeedStatus, " ", round(Speed, 1), " RPM (Z:", round(SpeedZScore, 1), ")"),
    ["Temperature"]         = strcat(TempStatus, " ", round(Temperature, 1), "Â°F (Z:", round(TempZScore, 1), ")"),
    ["Vibration"]           = strcat(VibrationStatus, " ", round(Vibration, 3), " (Z:", round(VibZScore, 1), ")"),
    ["Defect Probability"]  = strcat(DefectStatus, " ", round(DefectProbability * 100, 1), "%"),
       ["Last Update"]         = format_datetime(Timestamp, "HH:mm:ss")
| order by Asset asc
| render table

// =========================
// tile: anomaly rate
// purpose: show anomaly rate percentage per asset over time
// refresh cadence: every 30 seconds
// =========================

// -------------------------
// 1) configuration & parameters
// -------------------------
let baselinewindow = ago(30d); // use 30 days for statistical baseline
let assetfilterparam = tostring(['AssetFilter']); // optional asset filter
let redzthreshold = 2.0;       // z-score threshold for anomaly detection
let binsize = 1d;              // time bin for anomaly rate calculation

// -------------------------
// 2) asset mapping (id -> name)
// -------------------------
let assetmapping =
    assets
    | project AssetId = Id, assetname = Name;

// -------------------------
// 3) calculate baseline statistics (exclude last 24h for stability)
// -------------------------
let sensorbaseline =
    events
    | join kind=inner (assetmapping) on AssetId
    | where Timestamp >= baselinewindow and Timestamp <= ago(24h)
    | where isempty(assetfilterparam) or assetname == assetfilterparam
    | summarize
        speedmean = avg(Speed), speedstdev = stdev(Speed),
        tempmean = avg(Temperature), tempstdev = stdev(Temperature),
        vibrationmean = avg(Vibration), vibrationstdev = stdev(Vibration)
      by AssetId;

// -------------------------
// 4) detect anomalies with z-score logic
// -------------------------
events
| join kind=inner (assetmapping) on AssetId
| where Timestamp >= _startTime and Timestamp <= _endTime
| where isempty(assetfilterparam) or assetname == assetfilterparam
| join kind=inner (sensorbaseline) on AssetId
| extend
    speedzscore = iff(isnotnull(speedstdev) and speedstdev > 0, todecimal(abs(Speed - speedmean) / speedstdev), todecimal(0)),
    tempzscore = iff(isnotnull(tempstdev) and tempstdev > 0, todecimal(abs(Temperature - tempmean) / tempstdev), todecimal(0)),
    vibrationzscore = iff(isnotnull(vibrationstdev) and vibrationstdev > 0, todecimal(abs(Vibration - vibrationmean) / vibrationstdev), todecimal(0))
|extend 
    speedanomaly = speedzscore > redzthreshold,
    tempanomaly = tempzscore > redzthreshold,
    vibrationanomaly = vibrationzscore > redzthreshold,
    qualityanomaly = DefectProbability > 0.05
| summarize
    speedanomalycount = countif(speedanomaly),
    tempanomalycount = countif(tempanomaly),
    vibrationanomalycount = countif(vibrationanomaly),
    qualityanomalycount = countif(qualityanomaly),
    totalanomalies = countif(speedanomaly or tempanomaly or vibrationanomaly or qualityanomaly),
    eventcount = count()
    by AssetId, assetname, timewindow = bin(Timestamp, binsize)
| extend anomalyrate = round(totalanomalies * 100.0 / eventcount, 1)
| project timewindow, anomalyrate, assetname| project timewindow, anomalyrate, assetname

// =========================
// tile: asset qualiy metrics
// purpose: compare asset Anomaly Rate% and Quality Issues% side-by-side for operational insights
// refresh cadence: every 30 seconds
// =========================

// -------------------------
// 1) configuration & parameters
// -------------------------
let baselinewindow = ago(30d); // use 30 days for statistical baseline
let assetfilterparam = tostring(['AssetFilter']); // optional asset filter

// -------------------------
// 2) asset mapping (id -> name)
// -------------------------
let assetmapping =
    assets
    | project AssetId = Id, assetname = Name;
// -------------------------
// 3) calculate baseline statistics (exclude last 24h for stability)
// -------------------------
let sensorbaseline =
    events
    | join kind=inner (assetmapping) on AssetId
    | where Timestamp >= baselinewindow and Timestamp <= ago(24h)
    | where isempty(assetfilterparam) or assetname == assetfilterparam
    | summarize
        speedmean = avg(Speed), speedstdev = stdev(Speed),
        tempmean = avg(Temperature), tempstdev = stdev(Temperature),
        vibrationmean = avg(Vibration), vibrationstdev = stdev(Vibration)
      by AssetId;
// -------------------------
// 4) analyze recent performance
// -------------------------
events
| join kind=inner (assetmapping) on AssetId
| where Timestamp >= _startTime and Timestamp <= _endTime
| where isempty(assetfilterparam) or assetname == assetfilterparam
| join kind=leftouter (sensorbaseline) on AssetId
| extend
    speedanomaly = iff(isnotnull(speedstdev) and speedstdev > 0, abs(Speed - speedmean) / speedstdev > 2.0, false),// Calculating Speed Z score
    tempanomaly = iff(isnotnull(tempstdev) and tempstdev > 0, abs(Temperature - tempmean) / tempstdev > 2.0, false), // Calculating Temp Z score
    vibrationanomaly = iff(isnotnull(vibrationstdev) and vibrationstdev > 0, abs(Vibration - vibrationmean) / vibrationstdev > 2.0, false), // Calculating Vibration Z score
    qualityissue = DefectProbability > 0.05
| summarize
    totalevents = count(),
    speedanomalies = countif(speedanomaly),
    tempanomalies = countif(tempanomaly),
    vibrationanomalies = countif(vibrationanomaly),
    qualityissues = countif(qualityissue),
    avgspeed = round(avg(Speed), 1),
    avgtemp = round(avg(Temperature), 1),
    avgvibration = round(avg(Vibration), 3),
    avgdefectprob = round(avg(DefectProbability) * 100, 1)
    by assetname
| extend
    anomalyrate = round((speedanomalies + tempanomalies + vibrationanomalies) * 100.0 / totalevents, 1),
    qualityrate = round(qualityissues * 100.0 / totalevents, 1)
| extend performancescore = case(
    anomalyrate < 5 and qualityrate < 10, "ðŸŸ¢ excellent",
    anomalyrate < 10 and qualityrate < 20, "ðŸŸ¡ good",
    "ðŸ”´ needs attention"
)
| project
    assetname,
    ["anomaly rate %"] = anomalyrate,
    ["quality issues %"] = qualityrate
| order by assetname asc
| render columnchart
    with (
        title = "ðŸ“Š asset performance comparison: anomaly vs quality issues",
        xtitle = "asset",
        ytitle = "percentage (%)",
        legend = visible
    )

// =========================
// Tile: Speed Trend Over Time (30 Days)
// Purpose: Show average speed trend in time bins for the last 30 days
// Refresh cadence: Every 30 seconds
// =========================

// -------------------------
// 1) Configuration & Parameters (same naming as previous query)
// -------------------------
let BaselineWindow        = 30d;        // Lookback period for trend analysis
let BinSize               = 1h;         // Time bin size for aggregation
let AssetFilterParam      = tostring(['AssetFilter']); // Optional asset filter

// -------------------------
// 2) Asset mapping (Id -> Name)
// -------------------------
let assetMapping =
    assets
    | project AssetId = Id, AssetName = Name;

// -------------------------
// 3) Trend calculation
//    Aggregate average speed per asset in hourly bins
// -------------------------
events
| join kind=inner (assetMapping) on AssetId
| where Timestamp >= _startTime and Timestamp <= _endTime
| where isempty(AssetFilterParam) or AssetName == AssetFilterParam
| summarize avgSpeed = round(avg(Speed), 2) by AssetName, timeBin = bin(Timestamp, BinSize)
// -------------------------
// 4) Presentation: timechart
// -------------------------
| order by timeBin asc
| render timechart
       with (
        title  = "ðŸ“ˆ Speed Trend Over Time (Last 30 Days)",
        xtitle = "Time",
        ytitle = "Average Speed (RPM)"
       )

